import React from "react";
import { List, Avatar } from "antd";

class SellPage extends React.Component {
    state={
        data: [
            { id:0,title: "Rice", quantity: "40rs/KG" , imgSrc:"https://bethlehemplace.org/wp-content/uploads/2013/06/81defb3758f2613d213f771d297601bd_salt-clipart-sack-rice-pencil-and-in-color-salt-clipart-sack-rice-rice-clipart-transparent_360-352.png",qty:0},
            { id:1,title: "Wheat", quantity: "20rs/KG" , imgSrc:"http://desifoodkart.com/wp-content/uploads/2019/02/Chakki-Atta-Flour.jpg",qty:0},
            { id:2,title: "Tomato", quantity: "30rs/KG" , imgSrc:"http://desifoodkart.com/wp-content/uploads/2019/02/Chakki-Atta-Flour.jpg",qty:0}
          ]
    }

    addQuantity = (id)=>{
        const dataClone = this.state.data;
        let dataPosition;
        dataClone.forEach((item,index)=>{
            if(item.id === id){
                dataPosition = index;
            }
        });
        dataClone[dataPosition].qty++;
        this.setState({data: dataClone});
        
    }

  render() {
      const {data} = this.state;
    return (
      <>
        Sell Page
        <div style={{margin:'0px 20%'}}>
          <List
            itemLayout="horizontal"
            bordered
            dataSource={data}
            renderItem={item => (
              <List.Item actions={[<a key="list-loadmore-edit" onClick={()=>this.addQuantity(item.id)}>add</a>, <a key="list-loadmore-more">delete</a>]}>
                <List.Item.Meta
                  avatar={
                    <Avatar src={item.imgSrc} />
                  }
                  title={<a href="https://ant.design">{item.title}</a>}
                  description={item.quantity}
                />
                <div>{item.qty}</div>
              </List.Item>
            )}
          />
        </div>
        }
      </>
    );
  }
}

export default SellPage;
